//solium-disable linebreak-style
pragma solidity ^0.4.24;

contract Soldiers {
    // Modelo de soldado
    struct Soldier {
        uint id;
        string name;
        string class;
        uint level;
    }

    // Armazena candidatos

    // Mapeia informações dos soldados
    mapping(uint => Soldier) public soldiers;

    // Armazena quantidade de soldados
    uint public soldiersCount;

    // Construtor
    constructor() public {
        addSoldier("Augusto", "Guerreiro", 102);
    
    }

    function addSoldier(string _name, string _class, uint _level) private {
        soldiersCount++;
        soldiers[soldiersCount] = Soldier(soldiersCount, _name, _class, _level); 
    }
}
